import React, { useContext, useState } from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import { Context } from "../../context/context";
import { useParams } from "react-router-dom";

const Hero = () => {
  // const Array = [1, 2, 3, 4, 5];
  // const slicedArray = originalArray.slice(1, 4);
  const { listProducts } = useContext(Context);
  const { id } = useParams();
  const [search, setSearch] = useState("");

  console.log(search);
  const card = listProducts?.find((item) => item.id == id);

  return (
    <div className="h-container w-full flex gap-y-8 justify-center items-center flex-wrap">
      <input
        className="search-bar absolute gap-4 top-1 left-0"
        onChange={(e) => setSearch(e.target.value)}
        type="text"
      />
      <img
        src="https://theme.hstatic.net/200000411281/1000949882/14/home_slider_image_1.jpg?v=240"
        height={100}
      />
    </div>
  );
};

export default Hero;
