import React, { useContext } from "react";
import { Context } from "../../context/context";

const Cart = () => {
  const { listProducts } = useContext(Context);
  const cartArray = JSON.parse(localStorage.getItem("cartitem")) || [];
  console.log(cartArray);

  const results = cartArray.map((item) => {
    const listFind = listProducts?.find((itemList) => {
      return itemList.id === item.id;
    });
    return listFind;
  });
  return <div className="bg-red-200 h-[500px]"></div>;
};

export default Cart;
