import { Swiper, SwiperSlide } from "swiper/react";
import { Context } from "../../context/context";
import { useParams } from "react-router-dom";
import "./Hero.css";
const Hero = () => {
  // const Array = [1, 2, 3, 4, 5];
  // const slicedArray = originalArray.slice(1, 4);
  const { listProducts } = useContext(Context);
  const { id } = useParams();
  const [search, setSearch] = useState("");

  console.log(search);
  const card = listProducts?.find((item) => item.id == id);

  return (
    <div className="hero-container w-full flex gap-y-8 justify-center items-center flex-wrap">
      {listProducts?.map((item) => {
        return (
          <div className="flex justify-center items-center flex-col gap-8 ">
            <input
              className="search-bar absolute left-8 top-12"
              onChange={(e) => setSearch(e.target.value)}
              type="text"
              placeholder="Tìm kiếm bánh..."
            />
            <div className="flex justify-center items-center ">
              hiển thị danh sách sp
            </div>
          </div>
        );
      })}
      <img
        src="https://theme.hstatic.net/200000411281/1000949882/14/home_slider_image_1.jpg?v=240"
        height={100}
      />
    </div>
  );
};

export default Hero;
