import React, { useContext, useEffect, useState } from "react";
import { Context } from "../../context/context";
import { useParams } from "react-router-dom";

const Cart = () => {
  const { listProducts } = useContext(Context);
  const { id } = useParams();

  const [quantity, setQuantity] = useState(1);
  const [price, setPrice] = useState(null);

  const card = listProducts?.find((item) => item.id === +id);

  // Lấy danh sách mặt hàng từ Local Storage hoặc sử dụng mảng rỗng nếu không có
  const [cartArray, setCartArray] = useState(
    JSON.parse(localStorage.getItem("cartitem")) || []
  );

  // Tạo một hiệu ứng sẽ được gọi khi giá trị của cartArray thay đổi
  useEffect(() => {
    // Lưu danh sách mặt hàng vào Local Storage
    localStorage.setItem("cartitem", JSON.stringify(cartArray));
  }, [cartArray]);

  const handleRenderCart = (cardId) => {
    // Tìm sản phẩm trong danh sách mặt hàng
    const product = listProducts?.find((item) => item.id === +cardId);
    if (product) {
      // Nếu sản phẩm tồn tại, thêm sản phẩm vào giỏ hàng
      const newCartItem = {
        id: product.id,
        quantity: 1,
        price: product.price,
        name: product.name,
        image: product.image,
      };
      setCartArray([...cartArray, newCartItem]);
    }
    // Kiểm tra xem sản phẩm đã tồn tại trong giỏ hàng chưa
    const existingProductIndex = cartArray.findIndex(
      (item) => item.id === +cardId
    );

    if (existingProductIndex !== -1) {
      // Nếu sản phẩm đã tồn tại, cập nhật số lượng
      const updatedCartArray = [...cartArray];
      updatedCartArray[existingProductIndex].quantity += 1;
      setCartArray(updatedCartArray);
    } else {
      // Nếu sản phẩm chưa tồn tại, thêm sản phẩm vào giỏ hàng
      const newCartItem = {
        id: product.id,
        quantity: 1,
        price: product.price,
      };
      setCartArray([...cartArray, newCartItem]);
    }
  };

  const handleIncreaseQuantity = (cartItem) => {
    const updatedCartArray = [...cartArray];
    const index = updatedCartArray.findIndex((item) => item.id === cartItem.id);

    if (index !== -1) {
      updatedCartArray[index].quantity += 1;
      setCartArray(updatedCartArray);
    }
  };

  const handleDecreaseQuantity = (cartItem) => {
    const updatedCartArray = [...cartArray];
    const index = updatedCartArray.findIndex((item) => item.id === cartItem.id);

    if (index !== -1) {
      if (updatedCartArray[index].quantity > 1) {
        updatedCartArray[index].quantity -= 1;
        setCartArray(updatedCartArray);
      }
    }
  };

  const handleRemoveItem = (cartItem) => {
    const updatedCartArray = cartArray.filter(
      (item) => item.id !== cartItem.id
    );
    setCartArray(updatedCartArray);
  };

  return (
    <div className="bg-red-200 h-[500px]">
      <div className="flex flex-col gap-5 justify-center">
        {cartArray.length === 0 ? (
          <p>Không có sản phẩm nào trong giỏ hàng</p>
        ) : (
          cartArray.map((item, index) => (
            <div className="cart-item gap-8 flex items-center" key={index}>
              <img src={item?.image} className="img-card" />
              <div>Tên sản phẩm: {item?.name}</div>
              <div>Giá: {item?.price}</div>
              <div className="flex-col">
                <div className="flex">
                  <button
                    className="button"
                    onClick={() => handleDecreaseQuantity(item)}
                  >
                    -
                  </button>
                  <div className="border-none font-bold pr-6 bg-slate-200">
                    {item?.quantity}
                  </div>
                  <button
                    className="button"
                    onClick={() => handleIncreaseQuantity(item)}
                  >
                    +
                  </button>
                </div>
              </div>
              <div>Tổng Giá Của Sản Phẩm: {item?.quantity * item?.price}</div>
              <button
                className="button2 mt-4"
                onClick={() => handleRemoveItem(item)}
              >
                Xóa Sản Phẩm
              </button>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Cart;
