import React, { useContext, useEffect, useState } from "react";
import { Context } from "../../context/context";
import { Link, useParams } from "react-router-dom";
import "./Search.css";

const SearchCard = () => {
  const { listProducts } = useContext(Context);
  const { id } = useParams();
  const [search, setSearch] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  // Filter the products based on the search input
  const filteredProducts = listProducts.filter((product) => {
    const queryLower = search.toLowerCase();
    const { name } = product;

    const nameMatch = name.toLowerCase().includes(queryLower);

    return nameMatch;
  });

  return (
    <div>
      <div className="flex justify-center items-center flex-col p-6 w-full gap-8">
        <input
          className="search-bar"
          onChange={(e) => setSearch(e.target.value)}
          type="text"
          placeholder="Tìm kiếm bánh..."
        />
      </div>

      <div className="p-6 flex gap-y-8 justify-center items-center flex-wrap">
        {search.trim() !== "" ? (
          // Display products only if the search input is not empty
          filteredProducts.map((product) => (
            <div key={product.id}>
              <p>
                <Link to={`/card/${product.id}`}>
                  <div>{product.name}</div>
                  <img src={product.image} alt={product.name} />
                </Link>
              </p>
            </div>
          ))
        ) : (
          // Display a message if the search input is empty
          <p>Vui lòng nhập tên sản phẩm để tìm kiếm.</p>
        )}
      </div>
    </div>
  );
};

export default SearchCard;
