import React, { useContext, useState } from "react";
import { Context } from "../../context/context";
import { useParams } from "react-router-dom";

const Cart = () => {
  const { listProducts } = useContext(Context);
  const { id } = useParams();

  // State để quản lý số lượng và giá
  const [quantity, setQuantity] = useState(1);
  const [price, setPrice] = useState(null);

  // Tìm sản phẩm dựa trên ID được cung cấp
  const card = listProducts?.find((item) => item.id === +id);

  // Tạo một đối tượng với số lượng và giá hiện tại
  const currentCartValue = { quantity, price };
  console.log(currentCartValue);
  // Lấy danh sách mặt hàng trong giỏ hàng từ localStorage hoặc khởi tạo một mảng trống
