import React, { useContext, useState } from "react";

import { Context } from "../../context/context";
import { useParams } from "react-router-dom";
import "./Search.css";
const SearchCard = () => {
  const { listProducts } = useContext(Context);
  const { id } = useParams();
  const [search, setSearch] = useState("");

  console.log(search);
  const card = listProducts?.find((item) => item.id == id);

  return (
    <div>
      {listProducts?.map((item) => {
        return (
          <div className="flex justify-center items-center flex-col p-6 w-full gap-8">
            <input
              className="search-bar"
              onChange={(e) => setSearch(e.target.value)}
              type="text"
              placeholder="Tìm kiếm bánh..."
            />
            <div className="p-6 flex gap-y-8 justify-center items-center flex-wrap "></div>
          </div>
        );
      })}
    </div>
  );
};

export default SearchCard;
