import React from "react";
import "./Residencies.css";
import { useContext } from "react";
import { Context } from "../../context/context";
import { Link } from "react-router-dom";
import PropertyCard from "../PropertyCard/PropertyCard";

const Residencies = () => {
  const { listProducts, category } = useContext(Context);

  const getCakeCategoryName = (categoryId) => {
    const cakeCategory = category.find((cat) => cat.id === categoryId);
    return cakeCategory ? cakeCategory.name : "Danh mục không xác định";
  };

  return (
    <div className="bg-red-200 py-4">
      <div className="r-container gap-4">
        {category.map((cat) => (
          <div key={cat.id}>
            <div className="flex flex-col">
              <span className="text-[#8c8b8b] text-[1.5rem]">{cat.name}</span>
            </div>
            <div className="flex justify-center items-center">
              {listProducts
                .filter((product) => product.categoryId === cat.id)
                .map((card) => (
                  <div key={card.id}>
                    <Link
                      to={`/card/${card.id}`}
                      className="flex flex-col gap-1 r-card"
                    >
                      <img src={card.image} className="img-card" />
                      <div>{card.name}</div>
                      <div>{card.price}</div>
                    </Link>
                  </div>
                ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Residencies;
