import React, { useState } from "react";
import { Button, Checkbox, Form, Input } from "antd";
import { Link } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";

const Login = () => {
  // Khởi tạo biến state để lưu tên đăng nhập và mật khẩu
  const [useNickname, setNickname] = useState("");
  const [usePassword, setPassword] = useState("");

  const submitHandle = () => {
    let isLoggedIn = false;
    axios
      .get("http://localhost:3004/users")
      .then((response) => {
        const data = response.data;
        data.forEach((user) => {
          const nickname = user.nickname;
          const password = user.password;

          if (nickname === useNickname && password === usePassword) {
            isLoggedIn = true;
            localStorage.setItem("isLogin", nickname);
          }
        });
        if (isLoggedIn) {
          toast.success("Đăng nhập thành công");
          // Chuyển hướng sau khi đăng nhập thành công
          window.location.href = "/";
        } else {
          toast.error("Sai tài khoản hoặc mật khẩu, vui lòng đăng nhập lại");
        }
      })
      .catch((error) => {
        console.error("Lỗi khi gửi yêu cầu get: ", error);
      });
  };

  return (
    <div>
      <div className="h-container flex gap-y-8 items-center flex-wrap w-full p-6 px-4 py-4 justify-between">
        {/* left side  */}
        <div className="left-side">
          <Link to="/">
            <img
              src="https://theme.hstatic.net/200000411281/1000949882/14/logo.png?v=229"
              width={300}
              alt="Logo"
            />
          </Link>
        </div>
        {/* right side  */}
        <div className="right-side justify-between">
          {/* menu  */}
          <div className="h-menu flex gap-y-8 justify-center items-center flex-wrap gap-8">
            <a>Liên Hệ</a>
            <a>65 Dương Tôn Hải, Đà Nẵng</a>
          </div>
        </div>
      </div>
      <div className="">
        <Form
          name="basic"
          labelCol={{
            span: 8,
          }}
          wrapperCol={{
            span: 16,
          }}
          style={{
            maxWidth: 600,
          }}
          initialValues={{
            remember: true,
          }}
          autoComplete="off"
        >
          <Form.Item
            label="Tài Khoản"
            name="username"
            rules={[
              {
                required: true,
                message: "Vui lòng nhập Tài Khoản!",
              },
            ]}
          >
            <Input
              value={useNickname}
              onChange={(e) => setNickname(e.target.value)}
              type="text"
              placeholder="Tài Khoản"
            />
          </Form.Item>

          <Form.Item
            label="Mật Khẩu"
            name="password"
            rules={[
              {
                required: true,
                message: "Vui lòng nhập Mật Khẩu!",
              },
            ]}
          >
            <Input.Password
              value={usePassword}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Mật Khẩu"
            />
          </Form.Item>

          <Form.Item
            name="remember"
            valuePropName="checked"
            wrapperCol={{
              offset: 8,
              span: 16,
            }}
          >
            <div>
              <Checkbox>Lưu Mật Khẩu</Checkbox>
              <Link to="/forgotpassword">Quên Mật Khẩu</Link>
            </div>
          </Form.Item>

          <button
            type="primary"
            htmlType="submit"
            onClick={submitHandle}
            className="button"
          >
            Đăng Nhập
          </button>
        </Form>
      </div>
    </div>
  );
};

export default Login;
