        email: userName,
        password: password,
      });
      toast.success("Cập nhật thành công !");
      navigate("/admin/user/");
    } catch (error) {
      console.error("Lỗi khi cập nhật dữ liệu:", error);
    }
  };
  const handleClick = () => {};

  return (
    <div className="flex justify-center bg-blue-200 h-96 ">
      <div className="flex flex-col gap-2 pt-6 ">
        <div>Tài Khoản: {isLogin}</div>
        <div>Tên: {name}</div>
        <input className="border rounded "></input>
        <div>Mật Khẩu: {password}</div>
        <input
          className="border rounded "
          placeholder="Nhập mật khẩu của bạn"
        ></input>
        <input
          className="border rounded "
          placeholder="Nhập lại mật khẩu của bạn"
        ></input>

        <div>Địa Chỉ:</div>
        <input
          className="border rounded"
          placeholder="Địa chỉ mới của bạn"
        ></input>
        <div>Số Điện Thoại Của Bạn:</div>
        <input></input>
        <button onClick={handleClick} type="">
          Lưu
        </button>
      </div>
    </div>
  );
};

export default Profile;
