import React, { useState } from "react";
import { Button, Checkbox, Form, Input } from "antd";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";

const Login = () => {
  const [useUsername, setUsername] = useState("");
  const [usePassword, setPassword] = useState("");
  const navigate = useNavigate();

  const submitHandle = () => {
    if (!useUsername || !usePassword) {
      // Check if either username or password is empty
      toast.error("Vui lòng nhập tài khoản và mật khẩu");
      return;
    }

    axios
      .get("http://localhost:3004/users")
      .then((response) => {
        const data = response.data;
        let loginSuccess = false; // New variable to track login success

        data.forEach((user) => {
          const username = user.username;
          const password = user.password;

          if (username === useUsername && password === usePassword) {
            // ... (rest of your login logic)
          }
        });

        // If login was not successful, display an error message
        if (!loginSuccess) {
          toast.error("Sai tài khoản hoặc mật khẩu, vui lòng đăng nhập lại");
        }
      })
      .catch((error) => {
        console.error("Lỗi khi gửi yêu cầu get: ", error);
        toast.error("Sai tài khoản hoặc mật khẩu, vui lòng đăng nhập lại");
      });
  };

  return (
    <div>
      <div className="h-container flex gap-y-8 items-center flex-wrap w-full p-6 px-4 py-4 justify-between">
        {/* ... (your header code) */}
      </div>
      <div className="">
        <Form
          name="basic"
          labelCol={{
            span: 8,
          }}
          wrapperCol={{
            span: 16,
          }}
          style={{
            maxWidth: 600,
          }}
          initialValues={{
            remember: true,
          }}
          autoComplete="off"
        >
          <Form.Item
            label="Tài Khoản"
            name="username"
            rules={[
              {
                required: true,
                message: "Vui lòng nhập Tài Khoản!",
              },
            ]}
          >
            <Input
              value={useUsername}
              onChange={(e) => setUsername(e.target.value)}
              type="text"
              placeholder="Tài Khoản"
            />
          </Form.Item>

          <Form.Item
            label="Mật Khẩu"
            name="password"
            rules={[
              {
                required: true,
                message: "Vui lòng nhập Mật Khẩu!",
              },
            ]}
          >
            <Input.Password
              value={usePassword}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Mật Khẩu"
            />
          </Form.Item>

          <Form.Item
            name="remember"
            valuePropName="checked"
            wrapperCol={{
              offset: 8,
              span: 16,
            }}
          >
            <div>
              <Checkbox>Lưu Mật Khẩu</Checkbox>
              <Link to="/forgotpassword">Quên Mật Khẩu</Link>
            </div>
          </Form.Item>
          <Form.Item
            wrapperCol={{
              offset: 8,
              span: 16,
            }}
          >
            <Button
              type="submit"
              className="bg-slate-500"
              onClick={submitHandle}
            >
              Submit
            </Button>
          </Form.Item>
        </Form>
      </div>
    </div>
  );
};

export default Login;
