import React from "react";

import { Button, Checkbox, Form, Input } from "antd";
import { Link } from "react-router-dom";
import axios from "axios";

const Login = () => {
  const nickname = "";
  const password = "";
  const [useEmail, setEmail] = useState("");
  const [usePassword, setPassword] = useState("");
  const isLogin = "1";
  const getDataUser = async (data) => {
    const responsive = await axios.get("http://localhost:3004/users", {
      nickname: data.nickname,
      password: data.password,
      role: "customer",
    });
    if (responsive.status === 200) {
    }
  };
  const onFinish = (values) => {
    console.log("test", values);
    const data = {
      nickname: values.nickname,
    };
  };
  return (
    <div>
      <div className="h-container flex gap-y-8  items-center flex-wrap w-full p-6 px-4 py-4 justify-between">
        {/* left side  */}
        <div className="left-side ">
          <Link to="/">
            <img
              src="https://theme.hstatic.net/200000411281/1000949882/14/logo.png?v=229"
              width={300}
            />
          </Link>
        </div>
        {/* right side  */}
        <div className="right-side justify-between ">
          {/* menu  */}
          <div className="h-menu flex gap-y-8 justify-center items-center flex-wrap gap-8">
            <a>Liên Hệ</a>
            <Link to="/cart">Cart</Link>
            <a>65 Dương Tôn Hải, Đà Nẵng</a>
          </div>
        </div>
      </div>
      <div className="">
        <Form
          name="basic"
          labelCol={{
            span: 8,
          }}
          wrapperCol={{
            span: 16,
          }}
          style={{
            maxWidth: 600,
          }}
          initialValues={{
            remember: true,
          }}
          autoComplete="off"
        >
          <Form.Item
            label="Username"
            name="username"
            rules={[
              {
                required: true,
                message: "Please input your username!",
              },
            ]}
          >
            <Input />
          </Form.Item>

          <Form.Item
            label="Password"
            name="password"
            rules={[
              {
                required: true,
                message: "Please input your password!",
              },
            ]}
          >
            <Input.Password />
          </Form.Item>

          <Form.Item
            name="remember"
            valuePropName="checked"
            wrapperCol={{
              offset: 8,
              span: 16,
            }}
          >
            <div>
              <Checkbox>Lưu Mật Khẩu</Checkbox>
              <Link to="/forgotpassword">Quên Mật Khẩu</Link>
            </div>
          </Form.Item>

          <Form.Item
            wrapperCol={{
              offset: 8,
              span: 16,
            }}
          >
            <Button>Submit</Button>
          </Form.Item>
        </Form>
      </div>
    </div>
  );
};

export default Login;
