import React, { useState } from "react";
import { Button, Checkbox, Form, Input } from "antd";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";

const Login = () => {
  const [listUser, setListUser] = useState([]);
  const [useUsername, setUsername] = useState("");
  const [usePassword, setPassword] = useState("");
  const navigate = useNavigate();
  const [role, setRole] = useState("");
  const [errorMessage, setErrorMessage] = useState(""); // New state variable for error message

  const submitHandle = () => {
    axios
      .get("http://localhost:3004/users")
      .then((response) => {
        const data = response.data;
        let loginSuccess = false; // New variable to track login success

        data.forEach((user) => {
          const username = user.username;
          const password = user.password;

          if (username === useUsername && password === usePassword) {
            setRole(user.role);
            localStorage.setItem("isLogin", username);
            loginSuccess = true;

            if (role === "admin") {
              toast.success("Đăng nhập thành công");
              navigate("/admin");
            } else if (role === "seller") {
              toast.success("Đăng nhập thành công");
              navigate("/seller");
            } else if (role === "customer") {
              toast.success("Đăng nhập thành công");
              navigate("/");
            }
          }
        });

        // If login was not successful, display an error message
        if (!loginSuccess) {
          setErrorMessage(
            "Sai tài khoản hoặc mật khẩu, vui lòng đăng nhập lại"
          );
        }
      })
      .catch((error) => {
        console.error("Lỗi khi gửi yêu cầu get: ", error);
        toast.error("Sai tài khoản hoặc mật khẩu, vui lòng nhập lại");
      });
  };

  return (
    <div>
      {/* ... previous code ... */}
      <div className="">
        <Form
          name="basic"
          labelCol={{
            span: 8,
          }}
          wrapperCol={{
            span: 16,
          }}
          style={{
            maxWidth: 600,
          }}
          initialValues={{
            remember: true,
          }}
          autoComplete="off"
        >
          {/* ... other form fields ... */}
          <Form.Item
            wrapperCol={{
              offset: 8,
              span: 16,
            }}
          >
            <Button
              type="submit"
              className="bg-slate-500"
              onClick={submitHandle}
            >
              Submit
            </Button>
          </Form.Item>
        </Form>
        {/* Display error message */}
        {errorMessage && (
          <div style={{ color: "red", textAlign: "center" }}>
            {errorMessage}
          </div>
        )}
      </div>
    </div>
  );
};

export default Login;
