import React, { useContext } from "react";
import { Context } from "../../context/context";
import { Link } from "react-router-dom";

const Category = () => {
  const { listProducts } = useContext(Context);

  const sweetCake = listProducts.filter((item) => item.categorysId === 1);
  const saltedCake = listProducts.filter((item) => item.categorysId === 2);
  return (
    <div>
      <div>
        {sweetCake?.map((item) => {
          return(
            <div>
            <div>{item.name}</div>
            <img src={item.image}/>
            </div>
          ) ;
        })}
      </div>
      <div>
        {saltedCake?.map((item) => {
          return <div>{item.name}</div>;
        })}
      </div>
    </div>
  );
};

export default Category;
