  const { listProducts } = useContext(Context);
  const { id } = useParams();
  const [search, setSearch] = useState("");

  console.log(search);
  const card = listProducts?.find((item) => item.id == id);

  return (
    <div>
      {listProducts?.map((item) => {
        return (
          <div className="flex justify-center items-center flex-col  flex-1">
            <input
              className="search-bar absolute left-8 top-12"
              onChange={(e) => setSearch(e.target.value)}
              type="text"
              placeholder="Tìm kiếm bánh..."
            />
            <div className=" flex flex-col  absolute left-14">
              hiển thị danh sách sp
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default Search;
