import React, { useContext, useState } from "react";
import { Context } from "../../context/context";
import { useParams } from "react-router-dom";
import "./Search.css";

const SearchCard = () => {
  const { listProducts } = useContext(Context);
  const { id } = useParams();
  const [search, setSearch] = useState("");

  const card = listProducts?.find((item) => item.id == id);

  // Hàm để lọc danh sách sản phẩm dựa trên từ khóa tìm kiếm
  const filteredProducts = listProducts.filter((product) => {
    return product.name.toLowerCase().includes(search.toLowerCase());
  });

  return (
    <div>
      <div className="flex justify-center items-center flex-col p-6 w-full gap-8">
        <input
          className="search-bar"
          onChange={(e) => setSearch(e.target.value)}
          type="text"
          placeholder="Tìm kiếm bánh..."
        />
      </div>

      <div className="p-6 flex gap-y-8 justify-center items-center flex-wrap">
        {filteredProducts.map((product) => (
          <div key={product.id}>
            <img src={card?.image} />
            <p>
              <div>{card?.name}</div>
            </p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SearchCard;
