import React, { useContext } from "react";
import { Context } from "../../context/context";
import { Link } from "react-router-dom";

const Category = () => {
  const { listProducts } = useContext(Context);

  const sweetCake = listProducts.filter((card) => card.categorysId === 1);
  const saltedCake = listProducts.filter((card) => card.categorysId === 2);
  const cupCake = listProducts.filter((card) => card.categorysId === 3);
  const cheeseCake = listProducts.filter((card) => card.categorysId === 4);

  console.log(sweetCake, "bansh ngot");
  return (
    <div>
      <div>
        {sweetCake?.map((card) => {
          return (
            <div>
              <div>{card.name}</div>
              <img  src={card.image} className="img-card w-" />
            </div>
          );
        })}
      </div>

      <div>
        {saltedCake?.map((card) => {
          return (
            <div>
              <div>{card.name}</div>
              <img src={card.image} />
            </div>
          );
        })}
      </div>
      <div>
        {cupCake?.map((card) => {
          return (
            <div>
              <div>{card.name}</div>
              <img src={card.image} />
            </div>
          );
        })}
      </div>
      <div>
        {cheeseCake?.map((card) => {
          return (
            <div>
              <div>{card.name}</div>
              <img src={card.image} />
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Category;
