import React, { useContext, useEffect, useState } from "react";
import { Context } from "../../context/context";
import { Link, useParams } from "react-router-dom";
import "./Search.css";

const SearchCard = () => {
  const { listProducts } = useContext(Context);
  const { id } = useParams();
  const [search, setSearch] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  console.log("====================================");
  console.log(listProducts);
  console.log("====================================");
  // const card = listProducts?.find((item) => item.id == id);

  const filteredProducts = listProducts.filter((product) => {
    // return product.name.toLowerCase().includes(search.toLowerCase());
    const queryLower = search.toLowerCase();
    const { name, categoryId } = product;

    const nameMatch = name.toLowerCase().includes(queryLower);

    return nameMatch;
    setSearchResults(filteredProducts);
    //   });
  });
  console.log("====================================");
  console.log(filteredProducts);
  console.log("====================================");

  // const handleSearch = () => {
  //   const queryLower = searchQuery.toLowerCase();

  //   const filteredResults = listFilm.filter((film) => {
  //   const { name, main_actor, startDay, director } = film;

  //   const nameMatch = name.toLowerCase().includes(queryLower);
  //   const main_actorMatch = main_actor.toLowerCase().includes(queryLower);
  //   const startDayMatch = startDay.toLowerCase().includes(queryLower);
  //   const directorMatch = director.toLowerCase().includes(queryLower);

  //   return nameMatch || main_actorMatch || startDayMatch || directorMatch;
  //   });

  //   setSearchResults(filteredResults);
  //   setNoResults(filteredResults.length === 0 || searchQuery === ''  ; // Cập nhật biến trạng thái noResults
  //   };
  return (
    <div>
      <div className="flex justify-center items-center flex-col p-6 w-full gap-8">
        <input
          className="search-bar"
          onChange={(e) => setSearch(e.target.value)}
          type="text"
          placeholder="Tìm kiếm bánh..."
        />
      </div>

      <div className="p-6 flex gap-y-8 justify-center items-center flex-wrap">
        {filteredProducts.map((product) => {
          return (
            <div key={product.id}>
              <p>
                <Link to={`/card/${product.id}`}>
                  <div>{product.name}</div>
                  {/* <div>{product.image}</div> */}
                  <img src={product.image} />
                </Link>
              </p>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default SearchCard;
