import React, { useContext, useEffect, useState } from "react";
import { Context } from "../../context/context";
import { Link, useParams } from "react-router-dom";
import "./Search.css";

const SearchCard = () => {
  const { listProducts } = useContext(Context);

  const [search, setSearch] = useState("");
  const [filteredProducts, setFilteredProducts] = useState([]);

  useEffect(() => {
    const updatedFilteredProducts = listProducts.filter((product) => {
      return product.name.toLowerCase().includes(search.toLowerCase());
    });
    setFilteredProducts(updatedFilteredProducts);
  }, [search, listProducts]);

  return (
    <div>
      <div className="flex justify-center items-center flex-col p-6 w-full gap-8">
        <input
          className="search-bar"
          onChange={(e) => setSearch(e.target.value)}
          type="text"
          placeholder="Tìm kiếm bánh..."
        />
      </div>

      {search && filteredProducts.length === 0 && (
        <div className="text-center">Không tìm thấy sản phẩm phù hợp.</div>
      )}

      {search && filteredProducts.length > 0 && (
        <div className="p-6 flex gap-y-8 justify-center items-center flex-wrap">
          {filteredProducts.map((product) => {
            return (
              <div key={product.id}>
                <p>
                  <Link to={`/card/${product.id}`}>
                    <div>{product.name}</div>
                    <img src={product.image} alt={product.name} />
                  </Link>
                </p>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default SearchCard;
