import React, { useState, useEffect } from "react";
import "./Header.css";
import { Link } from "react-router-dom";

const Header = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [fullname, setFullName] = useState("");

  useEffect(() => {
    // Check if the user is logged in
    const isLogin = localStorage.getItem("isLogin");
    if (isLogin) {
      setIsLoggedIn(true);
      // Fetch the user's name from wherever it's stored (e.g., localStorage)
      const user = JSON.parse(localStorage.getItem("user"));
      if (user && user.name) {
        setFullName(user.name);
      }
    }
  }, []);

  const logOut = () => {
    localStorage.removeItem("isLogin");
    localStorage.removeItem("user");
    window.location.href = "/";
  };

  return (
    <div className="h-wrapper bg-blue-100">
      <div className="h-container flex gap-y-8 items-center flex-wrap w-full p-6 px-4 py-4 justify-between">
        <div className="left-side">
          <Link to="/">
            <img
              src="https://theme.hstatic.net/200000411281/1000949882/14/logo.png?v=229"
              width={300}
            />
          </Link>
        </div>
        <div className="right-side justify-between">
          <div
            className={`h-menu flex gap-y-8 justify-center items-center flex-wrap gap-8 ${
              isLoggedIn ? "display-user" : "display-guest"
            }`}
          >
            <a>Liên Hệ</a>
            <Link to="/cart">Cart</Link>
            <a>65 Dương Tôn Hải, Đà Nẵng</a>
            {isLoggedIn ? (
              <div className="user-info">
                <span>{FullName}</span>
                <button className="button" onClick={logOut}>
                  Đăng Xuất
                </button>
              </div>
            ) : (
              <div className="flex gap-4">
                <button className="button">
                  <Link to="/login">Đăng Nhập</Link>
                </button>
                <button className="button2">
                  <Link to="/register">Đăng Ký</Link>
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Header;
